%ejemplo umbralclc; clear; close allpkg load imageA = imread('imagen2.jpg');subplot(2,3,1);imshow(A)title('Imágen original');A = im2double(A); [m,n]=size(A);%Histograma de Asubplot(2,3,2)imhist(A)title("Histograma original")%Ruido1a1 = 0.2^2;B1 = A+a1*randn(m,n);subplot(2,3,3);imshow(B1)title('Imágen con ruido');B1 = im2double(B1);%Histograma de Asubplot(2,3,4)imhist(B1)title("Histograma con ruido")%Ruido2a1 = 0.5^2;B2 = A+a1*randn(m,n);subplot(2,3,5);imshow(B2)title('Imágen con ruido 2');B2 = im2double(B2);%Histograma de Asubplot(2,3,6)imhist(B2)title("Histograma con ruido")