clc; clear; close allA = imread('files/flores.bmp');subplot(1,2,1)imshow(A)title('Imagen Original')A = double(A);  % Tipo de operación (División)save('files/mat1_original.mat','A')[m, n] = size(A);C = mat2cell(A, 8 * ones(1, m/8), 8 * ones(1, n/8));comp = 35;for i = 1:m/8  for j = 1:n/8    T = cell2mat(C(i,j));    jpeg_comp(T, comp);    C(i,j) = T;  endfor
endforsave('files/mat2_comprimida.mat', 'C');for i = 1:m/8  for j = 1:n/8    T = cell2mat(C(i,j));    jpeg_decomp(T, comp);    C(i,j) = T;  endforendforD = cell2mat(C);D = uint8(D);save('files/mat3_descomprimida.mat', 'D');subplot(1,2,2)imshow(D)title('Imagen Descomprimida')