clear; clc; close all;pkg load image;% Original image: XX = imread("dab.jpg");subplot(1,3,1);imshow(X);title('(a) Original','FontSize',14);% Mask image: HH = imread("dab_mask.jpg");H(H<50)=0;H(H>=50)=255;subplot(1,3,2);imshow(H);title('(b) Mask','FontSize',14);XH = X + H;Y = zeros(size(X));% Applying the algorithm to each color channelY(:,:,1) = fast_digital_image_inpainting(XH(:,:,1), H, iters=100);Y(:,:,2) = fast_digital_image_inpainting(XH(:,:,2), H, iters=100);Y(:,:,3) = fast_digital_image_inpainting(XH(:,:,3), H, iters=100);% Output image: Ysubplot(1,3,3);imshow(im2uint8(Y));title('(c) Altered','FontSize',14);